import React, { useEffect, useState } from "react";
import { Routes, Route, Navigate, useNavigate } from "react-router-dom";
import Login from "./Login";
import Register from "./Register";
import service from "./service";
import "./App.css";

function App() {
  const navigate = useNavigate();
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userName, setUserName] = useState("");

  // ◊ë◊ì◊ô◊ß◊î ◊ê◊ù ◊î◊û◊©◊™◊û◊© ◊õ◊ë◊® ◊û◊ó◊ï◊ë◊®
  useEffect(() => {
    const user = service.getCurrentUser();
    if (user) {
      setIsAuthenticated(true);
      setUserName(user.userName || user.UserName || "");
    }
  }, []);

  const handleLoginSuccess = (user) => {
    setIsAuthenticated(true);
    setUserName(user.userName || user.UserName || "");
    navigate("/tasks");
  };

  const handleRegisterSuccess = (user) => {
    setIsAuthenticated(true);
    setUserName(user.userName || user.UserName || "");
    navigate("/tasks");
  };

  const handleLogout = () => {
    service.logout();
    setIsAuthenticated(false);
    setUserName("");
    navigate("/login");
  };

  return (
    <Routes>
      <Route
        path="/"
        element={<Navigate to={isAuthenticated ? "/tasks" : "/login"} replace />}
      />

      <Route
        path="/login"
        element={
          <Login
            onSuccess={handleLoginSuccess}
            goToRegister={() => navigate("/register")}
          />
        }
      />

      <Route
        path="/register"
        element={
          <Register
            onSuccess={handleRegisterSuccess}
            goToLogin={() => navigate("/login")}
          />
        }
      />

      <Route
        path="/tasks"
        element={
          isAuthenticated ? (
            <TodoPage userName={userName} onLogout={handleLogout} />
          ) : (
            <Navigate to="/login" replace />
          )
        }
      />

      <Route path="*" element={<Navigate to="/" replace />} />
    </Routes>
  );
}

// -------- ◊ì◊£ ◊î◊û◊©◊ô◊û◊ï◊™ --------
function TodoPage({ userName, onLogout }) {
  const [todos, setTodos] = useState([]);
  const [search, setSearch] = useState("");
  const [showAdd, setShowAdd] = useState(false);
  const [newTask, setNewTask] = useState("");
  const [editingId, setEditingId] = useState(null);
  const [editName, setEditName] = useState("");
  const [editComplete, setEditComplete] = useState(false);

  useEffect(() => {
    loadTasks();
  }, []);

  async function loadTasks() {
    try {
      const data = await service.getTasks();
      setTodos(data);
    } catch (err) {
      console.error("Error loading tasks", err);
    }
  }

  async function handleAddTask(e) {
    e.preventDefault();
    if (!newTask.trim()) return;

    try {
      await service.addTask(newTask.trim());
      setNewTask("");
      setShowAdd(false);
      await loadTasks();
    } catch (err) {
      console.error("Error adding task", err);
    }
  }

  function startEdit(todo) {
    setEditingId(todo.id);
    setEditName(todo.name);
    setEditComplete(!!todo.isComplete);
  }

  async function saveEdit() {
    if (!editName.trim()) return;
    try {
      await service.updateTaskFull(editingId, editName.trim(), editComplete);
      setEditingId(null);
      await loadTasks();
    } catch (err) {
      console.error("Error updating task", err);
    }
  }

  async function deleteTask(id) {
    try {
      await service.deleteTask(id);
      await loadTasks();
    } catch (err) {
      console.error("Error deleting task", err);
    }
  }

  const filtered = todos.filter((t) =>
    t.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="page-root">
      <div className="card todo-card fade-in">
        <header className="card-header">
          <div className="card-title-block">
            <h1 className="title">◊©◊ú◊ï◊ù, {userName || "◊û◊©◊™◊û◊©"} üëã</h1>
            <p className="subtitle">◊ú◊ï◊ó ◊î◊û◊©◊ô◊û◊ï◊™ ◊î◊ê◊ô◊©◊ô ◊©◊ú◊ö</p>
          </div>
          <button className="btn btn-outline" onClick={onLogout}>
            ◊î◊™◊†◊™◊ß◊ï◊™
          </button>
        </header>

        {/* ◊ó◊ô◊§◊ï◊© */}
        <div className="section">
          <label className="field-label">◊ó◊ô◊§◊ï◊© ◊û◊©◊ô◊û◊î</label>
          <input
            className="text-input"
            type="text"
            placeholder="◊î◊ß◊ú◊ô◊ì◊ô ◊©◊ù ◊û◊©◊ô◊û◊î ◊ú◊ó◊ô◊§◊ï◊©..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        </div>

        {/* ◊î◊ï◊°◊§◊™ ◊û◊©◊ô◊û◊î */}
        <div className="section">
          <button
            className="btn btn-primary full-width"
            onClick={() => setShowAdd((v) => !v)}
          >
            {showAdd ? "◊°◊í◊ï◊® ◊î◊ï◊°◊§◊™ ◊û◊©◊ô◊û◊î ‚úñ" : "‚ûï ◊î◊ï◊°◊§◊™ ◊û◊©◊ô◊û◊î"}
          </button>

          {showAdd && (
            <form className="add-form" onSubmit={handleAddTask}>
              <input
                className="text-input"
                type="text"
                placeholder="◊©◊ù ◊î◊û◊©◊ô◊û◊î ◊î◊ó◊ì◊©◊î..."
                value={newTask}
                onChange={(e) => setNewTask(e.target.value)}
                autoFocus
              />
              <button type="submit" className="btn btn-secondary">
                ◊©◊û◊ô◊®◊î
              </button>
            </form>
          )}
        </div>

        {/* ◊®◊©◊ô◊û◊™ ◊û◊©◊ô◊û◊ï◊™ */}
        <div className="section">
