import React, { useEffect, useState } from "react";
import service from "./service.js";
import "./App.css";

function App() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState("");
  const [searchTerm, setSearchTerm] = useState("");

  // ◊©◊ú◊ô◊§◊™ ◊õ◊ú ◊î◊û◊©◊ô◊û◊ï◊™ ◊©◊ú ◊î◊û◊©◊™◊û◊©
  async function getTodos() {
    try {
      const data = await service.getTasks();
      setTodos(data);
    } catch (err) {
      console.error("‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊©◊ú◊ô◊§◊™ ◊û◊©◊ô◊û◊ï◊™:", err);
    }
  }

  // ◊î◊ï◊°◊§◊™ ◊û◊©◊ô◊û◊î ◊ó◊ì◊©◊î
  async function addTodo(e) {
    e.preventDefault();
    if (!newTodo.trim()) return;
    try {
      await service.addTask(newTodo);
      setNewTodo("");
      await getTodos();
    } catch (err) {
      console.error("‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊î◊ï◊°◊§◊™ ◊û◊©◊ô◊û◊î:", err);
    }
  }

  // ◊°◊ô◊û◊ï◊ü ◊û◊©◊ô◊û◊î ◊õ◊î◊ï◊©◊ú◊û◊î / ◊ë◊ô◊ò◊ï◊ú ◊î◊©◊ú◊û◊î
  async function toggleComplete(todo) {
    try {
      await service.updateTask(todo.id, !todo.isComplete);
      await getTodos();
    } catch (err) {
      console.error("‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊¢◊ì◊õ◊ï◊ü ◊û◊©◊ô◊û◊î:", err);
    }
  }

  // ◊û◊ó◊ô◊ß◊™ ◊û◊©◊ô◊û◊î
  async function deleteTodo(id) {
    try {
      await service.deleteTask(id);
      await getTodos();
    } catch (err) {
      console.error("‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊û◊ó◊ô◊ß◊™ ◊û◊©◊ô◊û◊î:", err);
    }
  }

  // ◊ó◊ô◊§◊ï◊© ◊û◊©◊ô◊û◊î ◊ú◊§◊ô ◊ò◊ß◊°◊ò
  const filteredTodos = todos.filter((todo) =>
    todo.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  useEffect(() => {
    getTodos();
  }, []);

  return (
    <div className="app-container">
      <h1>‚ú® ◊®◊©◊ô◊û◊™ ◊î◊û◊©◊ô◊û◊ï◊™ ◊©◊ú◊ô ‚ú®</h1>

      <div className="add-task">
        <form onSubmit={addTodo}>
          <input
            type="text"
            placeholder="◊î◊ï◊°◊ô◊§◊ô ◊û◊©◊ô◊û◊î ◊ó◊ì◊©◊î..."
            value={newTodo}
            onChange={(e) => setNewTodo(e.target.value)}
          />
          <button type="submit">‚ûï ◊î◊ï◊°◊£</button>
        </form>
      </div>

      <div className="search-bar">
        <input
          type="text"
          placeholder="◊ó◊§◊©◊ô ◊û◊©◊ô◊û◊î..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      <ul className="todo-list">
        {filteredTodos.length === 0 ? (
          <p className="no-tasks">◊ê◊ô◊ü ◊û◊©◊ô◊û◊ï◊™ ◊ú◊î◊¶◊í◊î üí§</p>
        ) : (
          filteredTodos.map((todo) => (
            <li key={todo.id} className={todo.isComplete ? "completed" : ""}>
              <span
                className="todo-name"
                onClick={() => toggleComplete(todo)}
                title="◊ú◊ó◊¶◊ô ◊õ◊ì◊ô ◊ú◊°◊û◊ü ◊õ◊î◊ï◊©◊ú◊û◊î / ◊ú◊ë◊ò◊ú"
              >
                {todo.name}
              </span>
              <button
                className="delete-btn"
                onClick={() => deleteTodo(todo.id)}
                title="◊û◊ó◊ß◊ô ◊û◊©◊ô◊û◊î"
              >
                ‚ùå
              </button>
            </li>
          ))
        )}
      </ul>
    </div>
  );
}

export default App;
