import React, { useEffect, useState } from "react";
import service from "./service";
import { useNavigate } from "react-router-dom";

function App() {
  const [newTodo, setNewTodo] = useState("");
  const [todos, setTodos] = useState([]);
  const [searchId, setSearchId] = useState(""); // 🆕 שדה לחיפוש לפי ID
  const navigate = useNavigate();

  // 🧩 בדיקה אם המשתמש מחובר
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
    } else {
      getTodos();
    }
  }, []);

  // ✅ שליפת כל המשימות
  async function getTodos() {
    try {
      const todos = await service.getTasks();
      setTodos(todos);
    } catch (err) {
      console.log("שגיאה בשליפת משימות:", err);
    }
  }

  // ➕ הוספת משימה חדשה
  async function createTodo(e) {
    e.preventDefault();
    try {
      await service.addTask(newTodo);
      setNewTodo("");
      await getTodos();
    } catch (err) {
      console.log("שגיאה בהוספת משימה:", err);
    }
  }

  // ✅ עדכון מצב משימה
  async function updateCompleted(todo, isComplete) {
    try {
      await service.updateTask(todo.id, todo.name, isComplete);
      await getTodos();
    } catch (err) {
      console.log("שגיאה בעדכון משימה:", err);
    }
  }

  // ❌ מחיקת משימה
  async function deleteTodo(id) {
    try {
      await service.deleteTask(id);
      await getTodos();
    } catch (err) {
      console.log("שגיאה במחיקת משימה:", err);
    }
  }

  // 🚪 התנתקות
  function handleLogout() {
    localStorage.removeItem("token");
    navigate("/login");
  }

  // 🔍 חיפוש משימה לפי מזהה (id)
  async function findTodoById() {
    if (!searchId) {
      alert("הכניסי מזהה משימה לחיפוש");
      return;
    }
    try {
      const task = await service.getTaskById(searchId);
      if (task) {
        alert(`נמצאה משימה:\n${task.name} (הושלמה: ${task.isComplete ? "כן" : "לא"})`);
      } else {
        alert("לא נמצאה משימה עם המזהה הזה");
      }
    } catch (err) {
      console.log("שגיאה בחיפוש משימה לפי מזהה:", err);
      alert("שגיאה בעת החיפוש");
    }
  }

  return (
    <section className="todoapp">
      <header className="header">
        <h1>משימות</h1>
        <button onClick={handleLogout} className="logout-btn">
          🚪 התנתקות
        </button>

        <form onSubmit={createTodo}>
          <input
            className="new-todo"
            placeholder="הוסיפי משימה חדשה"
            value={newTodo}
            onChange={(e) => setNewTodo(e.target.value)}
          />
        </form>

        {/* 🆕 חיפוש לפי מזהה */}
        <div className="search-by-id">
          <input
            type="number"
            placeholder="חפשי משימה לפי מזהה (ID)"
            value={searchId}
            onChange={(e) => setSearchId(e.target.value)}
          />
          <button onClick={findTodoById}>🔍 חפש</button>
        </div>
      </header>

      <section className="main" style={{ display: "block" }}>
        <ul className="todo-list">
          {todos.map((todo) => (
            <li className={todo.isComplete ? "completed" : ""} key={todo.id}>
              <div className="view">
                <input
                  className="toggle"
                  type="checkbox"
                  checked={todo.isComplete}
                  onChange={(e) => updateCompleted(todo, e.target.checked)}
                />
                <label>{todo.name}</label>
                <button
                  className="destroy"
                  onClick={() => deleteTodo(todo.id)}
                ></button>
              </div>
            </li>
          ))}
        </ul>
      </section>
    </section>
  );
}

export default App;
