import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import api from "./service";

export default function Register() {
  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const nav = useNavigate();

  async function submit(e) {
    e.preventDefault();
    try {
      const { token } = await api.register(userName, password);
      localStorage.setItem("token", token); // כניסה אוטומטית אחרי הרשמה
      nav("/");
    } catch (e) {
      alert("שם משתמש כבר קיים או שגיאה אחרת");
    }
  }

  return (
    <div className="auth">
      <h2>הרשמה</h2>
      <form onSubmit={submit}>
        <input placeholder="שם משתמש" value={userName} onChange={e=>setUserName(e.target.value)} />
        <input type="password" placeholder="סיסמה" value={password} onChange={e=>setPassword(e.target.value)} />
        <button type="submit">הירשם</button>
      </form>
      <p>כבר רשום? <Link to="/login">התחבר</Link></p>
    </div>
  );
}
