import axios from 'axios';

const apiUrl = "http://localhost:5246/api/items";

export default {
  // ✅ שליפת כל המשימות
  getTasks: async () => {
    const response = await axios.get(apiUrl);
    return response.data;
  },

  // ✅ שליפה לפי מזהה
  getTaskById: async (id) => {
    const response = await axios.get(`${apiUrl}/${id}`);
    return response.data;
  },

  // ✅ הוספת משימה חדשה
  addTask: async (name) => {
    const newTask = { name, isComplete: false };
    const response = await axios.post(apiUrl, newTask);
    return response.data;
  },

  // ✅ עדכון משימה קיימת (זו הפונקציה שחסרה!)
  updateTask: async (id, name, isComplete) => {
    const response = await axios.put(`${apiUrl}/${id}`, { id, name, isComplete });
    return response.data;
  },

  // ✅ מחיקת משימה
  deleteTask: async (id) => {
    const response = await axios.delete(`${apiUrl}/${id}`);
    return response.data;
  }
};
