import React, { useEffect, useState } from "react";
import service from "./service.js";
import "./App.css";

function App() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [editingId, setEditingId] = useState(null);
  const [editText, setEditText] = useState("");

  // ◊©◊ú◊ô◊§◊™ ◊õ◊ú ◊î◊û◊©◊ô◊û◊ï◊™
  async function getTodos() {
    try {
      const data = await service.getTasks();
      setTodos(data);
    } catch (err) {
      console.error("‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊©◊ú◊ô◊§◊™ ◊û◊©◊ô◊û◊ï◊™:", err);
    }
  }

  // ◊î◊ï◊°◊§◊™ ◊û◊©◊ô◊û◊î ◊ó◊ì◊©◊î
  async function addTodo(e) {
    e.preventDefault();
    if (!newTodo.trim()) return;
    try {
      await service.addTask(newTodo);
      setNewTodo("");
      await getTodos();
    } catch (err) {
      console.error("‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊î◊ï◊°◊§◊™ ◊û◊©◊ô◊û◊î:", err);
    }
  }

  // ◊û◊ó◊ô◊ß◊™ ◊û◊©◊ô◊û◊î
  async function deleteTodo(id) {
    try {
      await service.deleteTask(id);
      await getTodos();
    } catch (err) {
      console.error("‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊û◊ó◊ô◊ß◊î:", err);
    }
  }

  // ◊°◊ô◊û◊ï◊ü ◊õ◊î◊ï◊©◊ú◊û◊î / ◊ë◊ô◊ò◊ï◊ú
  async function toggleComplete(todo) {
    try {
      const updated = {
        id: todo.id,
        name: todo.name,
        isComplete: !todo.isComplete,
      };
      await service.updateTaskFull(updated.id, updated.name, updated.isComplete);
      await getTodos();
    } catch (err) {
      console.error("‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊¢◊ì◊õ◊ï◊ü ◊û◊©◊ô◊û◊î:", err);
    }
  }

  // ◊û◊¢◊ë◊® ◊ú◊û◊¶◊ë ◊¢◊®◊ô◊õ◊î
  function startEdit(todo) {
    setEditingId(todo.id);
    setEditText(todo.name);
  }

  // ◊©◊û◊ô◊®◊™ ◊¢◊®◊ô◊õ◊î
  async function saveEdit(todo) {
    try {
      await service.updateTaskFull(todo.id, editText, todo.isComplete);
      setEditingId(null);
      setEditText("");
      await getTodos();
    } catch (err) {
      console.error("‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊©◊û◊ô◊®◊™ ◊¢◊®◊ô◊õ◊î:", err);
    }
  }

  // ◊ë◊ô◊ò◊ï◊ú ◊¢◊®◊ô◊õ◊î
  function cancelEdit() {
    setEditingId(null);
    setEditText("");
  }

  // ◊ó◊ô◊§◊ï◊©
  const filteredTodos = todos.filter((t) =>
    (t.name ?? "").toLowerCase().includes(searchTerm.toLowerCase())
  );

  useEffect(() => {
    getTodos();
  }, []);

  return (
    <div className="app-container">
      <h1>‚ú® ◊®◊©◊ô◊û◊™ ◊î◊û◊©◊ô◊û◊ï◊™ ◊©◊ú◊ô ‚ú®</h1>

      {/* ◊î◊ï◊°◊§◊™ ◊û◊©◊ô◊û◊î */}
      <form className="add-task" onSubmit={addTodo}>
        <input
          type="text"
          placeholder="◊î◊ï◊°◊ô◊§◊ô ◊û◊©◊ô◊û◊î ◊ó◊ì◊©◊î..."
          value={newTodo}
          onChange={(e) => setNewTodo(e.target.value)}
        />
        <button type="submit">‚ûï ◊î◊ï◊°◊£</button>
      </form>

      {/* ◊ó◊ô◊§◊ï◊© */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="◊ó◊§◊©◊ô ◊û◊©◊ô◊û◊î..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* ◊®◊©◊ô◊û◊™ ◊û◊©◊ô◊û◊ï◊™ */}
      <ul className="todo-list">
        {filteredTodos.length === 0 ? (
          <p className="no-tasks">◊ê◊ô◊ü ◊û◊©◊ô◊û◊ï◊™ ◊ú◊î◊¶◊í◊î üí§</p>
        ) : (
          filteredTodos.map((todo) => (
            <li key={todo.id} className={todo.isComplete ? "completed" : ""}>
              {editingId === todo.id ? (
                <>
                  <input
                    type="text"
                    value={editText}
                    onChange={(e) => setEditText(e.target.value)}
                  />
                  <button onClick={() => saveEdit(todo)}>üíæ ◊©◊û◊ï◊®</button>
                  <button onClick={cancelEdit}>‚ùå ◊ë◊ò◊ú</button>
                </>
              ) : (
                <>
                  <span
                    className="todo-name"
                    onClick={() => toggleComplete(todo)}
                    title="◊°◊û◊†◊ô ◊õ◊î◊ï◊©◊ú◊û◊î / ◊ë◊ò◊ú◊ô ◊î◊©◊ú◊û◊î"
                  >
                    {todo.name}
                  </span>
                  <div className="actions">
                    <button
                      className="edit-btn"
                      onClick={() => startEdit(todo)}
                      title="◊¢◊®◊ï◊ö"
                    >
                      ‚úèÔ∏è
                    </button>
                    <button
                      className="delete-btn"
                      onClick={() => deleteTodo(todo.id)}
                      title="◊û◊ó◊ß"
                    >
                      ‚ùå
                    </button>
                  </div>
                </>
              )}
            </li>
          ))
        )}
      </ul>
    </div>
  );
}

export default App;
