import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function Register({ onRegisterSuccess }) {
  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState(false);
  const [loading, setLoading] = useState(false);

  async function handleRegister(e) {
    e.preventDefault();

    if (password !== confirm) {
      setError("הסיסמאות אינן תואמות ❌");
      return;
    }

    try {
      setLoading(true);
      setError("");
      const res = await axios.post("http://localhost:5246/api/auth/register", {
        userName, // 👈 בדיוק כך השרת שלך מקבל
        passwordHash: password,
      });

      // שמירה בלוקאל סטורג'
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("userName", res.data.user.userName);

      // הודעת הצלחה
      setSuccess(true);

      // המתנה קלה לפני מעבר (עם בדיקה שהפונקציה קיימת)
      setTimeout(() => {
        if (typeof onRegisterSuccess === "function") {
          onRegisterSuccess();
        }
      }, 1000);
    } catch (err) {
      if (err.response && err.response.status === 409)
        setError("שם המשתמש כבר קיים במערכת ⚠️");
      else
        setError("אירעה שגיאה בהרשמה ❌");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="auth-container fade-in">
      <div className="auth-box">
        <h1 className="app-title">✨ Task Manager Cloud ✨</h1>
        <h2 className="auth-title">הרשמה למערכת 📝</h2>

        <form onSubmit={handleRegister}>
          <input
            type="text"
            placeholder="שם משתמש"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="סיסמה"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="אימות סיסמה"
            value={confirm}
            onChange={(e) => setConfirm(e.target.value)}
            required
          />

          {error && <p className="error">{error}</p>}
          {success && <p className="success">נרשמת בהצלחה! 🎉</p>}

          <button
            type="submit"
            className="btn login-btn"
            disabled={loading}
            style={{ cursor: loading ? "not-allowed" : "pointer" }}
          >
            {loading ? "⏳ נרשם..." : "📋 הירשם"}
          </button>
        </form>

        <p className="switch-text">
          כבר יש לך משתמש?{" "}
          <a href="/login" className="link-hover">
            להתחברות
          </a>
        </p>
      </div>
    </div>
  );
}

export default Register;
