import React, { useEffect, useState } from "react";
import { Routes, Route, Navigate, useNavigate } from "react-router-dom";
import Register from "./Register";
import Login from "./Login";
import service from "./service";
import "./App.css";

function App() {
  const navigate = useNavigate();
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    setIsAuthenticated(!!token);
  }, []);

  const handleRegisterSuccess = () => navigate("/login");

  const handleLoginSuccess = () => {
    setIsAuthenticated(true);
    navigate("/app");
  };

  const handleLogout = () => {
    service.logout();
    setIsAuthenticated(false);
    navigate("/login");
  };

  return (
    <Routes>
      <Route
        path="/"
        element={<Navigate to={isAuthenticated ? "/app" : "/login"} />}
      />
      <Route
        path="/register"
        element={<Register onRegisterSuccess={handleRegisterSuccess} />}
      />
      <Route path="/login" element={<Login onLoginSuccess={handleLoginSuccess} />} />
      <Route
        path="/app"
        element={
          isAuthenticated ? (
            <TodoApp onLogout={handleLogout} />
          ) : (
            <Navigate to="/login" />
          )
        }
      />
      <Route
        path="*"
        element={<h2 style={{ textAlign: "center" }}>404 - ◊î◊ì◊£ ◊ú◊ê ◊†◊û◊¶◊ê üòÖ</h2>}
      />
    </Routes>
  );
}

// ---------------------------------------------
// üß© ◊®◊õ◊ô◊ë ◊†◊ô◊î◊ï◊ú ◊î◊û◊©◊ô◊û◊ï◊™
// ---------------------------------------------
function TodoApp({ onLogout }) {
  const [todos, setTodos] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [search, setSearch] = useState("");
  const [editId, setEditId] = useState(null);
  const [editName, setEditName] = useState("");
  const user = service.getCurrentUser();

  useEffect(() => {
    loadTasks();
  }, []);

  async function loadTasks() {
    try {
      const data = await service.getTasks();
      setTodos(data);
    } catch (err) {
      console.error("◊©◊í◊ô◊ê◊î ◊ë◊ò◊¢◊ô◊†◊™ ◊î◊û◊©◊ô◊û◊ï◊™:", err);
    }
  }

  async function addTask(e) {
    e.preventDefault();
    if (!newTask.trim()) return;
    try {
      await service.addTask(newTask);
      setNewTask("");
      loadTasks();
    } catch (err) {
      console.error("◊©◊í◊ô◊ê◊î ◊ë◊î◊ï◊°◊§◊™ ◊û◊©◊ô◊û◊î:", err);
    }
  }

  async function toggleComplete(id, isComplete) {
    try {
      await service.updateTask(id, !isComplete);
      loadTasks();
    } catch (err) {
      console.error("◊©◊í◊ô◊ê◊î ◊ë◊¢◊ì◊õ◊ï◊ü ◊û◊©◊ô◊û◊î:", err);
    }
  }

  async function deleteTask(id) {
    try {
      await service.deleteTask(id);
      loadTasks();
    } catch (err) {
      console.error("◊©◊í◊ô◊ê◊î ◊ë◊û◊ó◊ô◊ß◊î:", err);
    }
  }

  async function updateTask(e) {
    e.preventDefault();
    if (!editName.trim()) return;
    try {
      await service.updateTaskFull(editId, editName, false);
      setEditId(null);
      setEditName("");
      loadTasks();
    } catch (err) {
      console.error("◊©◊í◊ô◊ê◊î ◊ë◊¢◊ì◊õ◊ï◊ü:", err);
    }
  }

  const filteredTodos = todos.filter((t) =>
    t.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="todo-'◊©◊®' fade-in">
      <header className="todo-header">
        <h1>‚ú® Task Manager Cloud ‚ú®</h1>
        <div className="user-info">
          <span>◊©◊ú◊ï◊ù, {user?.userName || "◊û◊©◊™◊û◊©"}</span>
          <button className="logout-btn" onClick={onLogout}>
            ◊î◊™◊†◊™◊ß üö™
          </button>
        </div>
      </header>

      <div className="task-controls">
        <form className="task-form" onSubmit={addTask}>
          <input
            type="text"
            placeholder="◊î◊ï◊°◊£ ◊û◊©◊ô◊û◊î ◊ó◊ì◊©◊î..."
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
          />
          <button type="submit" className="add-btn">
            ‚ûï ◊î◊ï◊°◊£
          </button>
        </form>

        <input
          type="text"
          className="search-box"
          placeholder="üîç ◊ó◊ô◊§◊ï◊© ◊û◊©◊ô◊û◊î..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>

      <ul className="task-list">
        {filteredTodos.map((todo) => (
          <li key={todo.id} className={`task-item ${todo.isComplete ? "completed" : ""}`}>
            {editId === todo.id ? (
              <form className="edit-form" onSubmit={updateTask}>
                <input
                  type="text"
                  value={editName}
                  onChange={(e) => setEditName(e.target.value)}
                  autoFocus
                />
                <button type="submit" className="save-btn">üíæ ◊©◊û◊ï◊®</button>
                <button onClick={() => setEditId(null)} className="cancel-btn">‚úñ ◊ë◊ò◊ú</button>
              </form>
            ) : (
              <>
                <span
                  className="task-name"
                  onClick={() => toggleComplete(todo.id, todo.isComplete)}
                  title="◊°◊û◊ü ◊õ◊î◊ï◊©◊ú◊ù"
                >
                  {todo.name}
                </span>
                <div className="task-actions">
                  <button
                    className="edit-btn"
                    onClick={() => {
                      setEditId(todo.id);
                      setEditName(todo.name);
                    }}
                    title="◊¢◊®◊ï◊ö"
                  >
                    ‚úèÔ∏è
                  </button>
                  <button
                    className="delete-btn"
                    onClick={() => deleteTask(todo.id)}
                    title="◊û◊ó◊ß"
                  >
                    ‚ùå
                  </button>
                </div>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
