import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [todos, setTodos] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [userName, setUserName] = useState(localStorage.getItem("userName") || "");

  const apiUrl = "http://localhost:5246/api/items";

  // ✅ בעת טעינת האפליקציה נטען את המשימות
  useEffect(() => {
    loadTasks();
  }, []);

  // 📥 טעינת המשימות מהשרת
  async function loadTasks() {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(apiUrl, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTodos(res.data);
    } catch (err) {
      console.error("שגיאה בטעינת משימות:", err);
    }
  }

  // ➕ הוספת משימה
  async function addTask(e) {
    e.preventDefault();
    if (!newTask.trim()) return;

    try {
      const token = localStorage.getItem("token");
      await axios.post(
        apiUrl,
        { name: newTask, isComplete: false },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setNewTask("");
      loadTasks();
    } catch (err) {
      console.error("שגיאה בהוספת משימה:", err);
    }
  }

  // ✅ עדכון מצב השלמה
  async function toggleComplete(id, isComplete) {
    try {
      const token = localStorage.getItem("token");
      await axios.put(
        `${apiUrl}/${id}`,
        { isComplete: !isComplete },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      loadTasks();
    } catch (err) {
      console.error("שגיאה בעדכון משימה:", err);
    }
  }

  // ✏️ עריכת שם משימה
  async function editTask(id, oldName) {
    const newName = prompt("ערוך משימה:", oldName);
    if (newName === null || newName.trim() === "") return;

    try {
      const token = localStorage.getItem("token");
      await axios.put(
        `${apiUrl}/${id}`,
        { name: newName },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      loadTasks();
    } catch (err) {
      console.error("שגיאה בעריכת משימה:", err);
    }
  }

  // ❌ מחיקת משימה
  async function deleteTask(id) {
    if (!window.confirm("למחוק את המשימה לצמיתות?")) return;
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`${apiUrl}/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      loadTasks();
    } catch (err) {
      console.error("שגיאה במחיקת משימה:", err);
    }
  }

  // 🚪 התנתקות
  function logout() {
    localStorage.removeItem("token");
    localStorage.removeItem("userName");
    window.location.href = "/login";
  }

  return (
    <div className="todo-wrapper fade-in">
      <header className="todo-header">
        <h1>✨ Task Manager ✨</h1>
        <div className="user-info">
          <span>שלום, {userName}</span>
          <button className="logout-btn" onClick={logout}>
            התנתק 🚪
          </button>
        </div>
      </header>

      {/* הוספת משימה */}
      <form className="task-form" onSubmit={addTask}>
        <input
          type="text"
          placeholder="הוסף משימה חדשה..."
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <button type="submit" className="add-btn">
          ➕
        </button>
      </form>

      {/* רשימת המשימות */}
      <ul className="task-list">
        {todos.length === 0 ? (
          <p style={{ color: "#777" }}>אין משימות כרגע 😊</p>
        ) : (
          todos.map((todo) => (
            <li
              key={todo.id}
              className={`task-item ${todo.isComplete ? "completed" : ""}`}
            >
              <span
                className="task-name"
                onClick={() => toggleComplete(todo.id, todo.isComplete)}
                title="סמן כהושלם"
              >
                {todo.name}
              </span>
              <div>
                <button
                  className="edit-btn"
                  onClick={() => editTask(todo.id, todo.name)}
                  title="ערוך משימה"
                >
                  ✏️
                </button>
                <button
                  className="delete-btn"
                  onClick={() => deleteTask(todo.id)}
                  title="מחק משימה"
                >
                  🗑️
                </button>
              </div>
            </li>
          ))
        )}
      </ul>

      <div className="footer">© Task Manager Cloud 2025</div>
    </div>
  );
}

export default App;
