  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  async function handleLogin(e) {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5246/api/auth/login", {
        userName,
        passwordHash: password, 
      });
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("userName", res.data.user.userName);
      onLoginSuccess();
    } catch (err) {
      setError("◊©◊ù ◊û◊©◊™◊û◊© ◊ê◊ï ◊°◊ô◊°◊û◊î ◊©◊í◊ï◊ô◊ô◊ù ‚ùå");
    }
  }

  return (
    <div className="auth-container fade-in">
      <div className="auth-box">
        <h1 className="app-title">‚ú® Task Manager Cloud ‚ú®</h1>
        <h2 className="auth-title">◊ë◊®◊ï◊õ◊î ◊î◊©◊ë◊î üëã</h2>

        <form onSubmit={handleLogin}>
          <input
            type="text"
            placeholder="◊©◊ù ◊û◊©◊™◊û◊©"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="◊°◊ô◊°◊û◊î"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          {error && <p className="error">{error}</p>}

          <button type="submit" className="btn login-btn">
            ◊î◊™◊ó◊ë◊®
          </button>
        </form>

        <p className="switch-text">
          ◊ê◊ô◊ü ◊ú◊ö ◊û◊©◊™◊û◊©? <a href="/register">◊¶◊ï◊® ◊ê◊ó◊ì ◊ó◊ì◊©</a>
        </p>
      </div>
    </div>
  );
}

export default Login;
