                </div>
              ) : (
                <>
                  <span
                    className="task-name"
                    onClick={() => toggleComplete(todo)}
                    title="◊°◊û◊†◊ô ◊õ◊î◊ï◊©◊ú◊û◊î / ◊ë◊ò◊ú◊ô"
                  >
                    {todo.name}
                  </span>
                  <div className="actions">
                    <button className="btn edit" onClick={() => startEdit(todo)}>
                      ‚úèÔ∏è
                    </button>
                    <button
                      className="btn delete"
                      onClick={() => deleteTodo(todo.id)}
                    >
                      üóëÔ∏è
                    </button>
                  </div>
                </>
