import axios from 'axios';

const apiUrl = "http://localhost:5246/api/items";

export default {
  //  砖驻转  砖转
  getTasks: async () => {
    const response = await axios.get(apiUrl);
    return response.data;
  },

  //  住驻转 砖 砖
  addTask: async (name) => {
    const newTask = { name, isComplete: false };
    const response = await axios.post(apiUrl, newTask);
    return response.data;
  },

  //  注 住住 砖 (住 砖 / )
  setCompleted: async (id, isComplete) => {
    const response = await axios.put(`${apiUrl}/${id}`, { id, isComplete });
    return response.data;
  },

  //  拽转 砖 驻 ID
  deleteTask: async (id) => {
    const response = await axios.delete(`${apiUrl}/${id}`);
    return response.data;
  }
};
