import React, { useEffect, useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
  useNavigate,
} from "react-router-dom";
import Register from "./Register";
import Login from "./Login";
import axios from "axios";
import "./App.css";

function App() {
  return (
    <Router>
      <MainRoutes />
    </Router>
  );
}

function MainRoutes() {
  const navigate = useNavigate();
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    setIsAuthenticated(!!token);
  }, []);

  // üîπ ◊û◊¢◊ë◊® ◊ê◊ó◊®◊ô ◊î◊®◊©◊û◊î
  const handleRegisterSuccess = () => {
    navigate("/login");
  };

  // üîπ ◊û◊¢◊ë◊® ◊ê◊ó◊®◊ô ◊î◊™◊ó◊ë◊®◊ï◊™
  const handleLoginSuccess = () => {
    setIsAuthenticated(true);
    navigate("/app");
  };

  // üîπ ◊î◊™◊†◊™◊ß◊ï◊™
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userName");
    setIsAuthenticated(false);
    navigate("/login");
  };

  return (
    <Routes>
      <Route
        path="/"
        element={<Navigate to={isAuthenticated ? "/app" : "/login"} />}
      />
      <Route
        path="/register"
        element={<Register onRegisterSuccess={handleRegisterSuccess} />}
      />
      <Route
        path="/login"
        element={<Login onLoginSuccess={handleLoginSuccess} />}
      />
      <Route
        path="/app"
        element={
          isAuthenticated ? (
            <TodoApp onLogout={handleLogout} />
          ) : (
            <Navigate to="/login" />
          )
        }
      />
      <Route
        path="*"
        element={
          <h2 style={{ textAlign: "center", marginTop: "40px" }}>
            404 - ◊î◊ì◊£ ◊ú◊ê ◊†◊û◊¶◊ê üòÖ
          </h2>
        }
      />
    </Routes>
  );
}

function TodoApp({ onLogout }) {
  const [todos, setTodos] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [userName] = useState(localStorage.getItem("userName") || "");

  const apiUrl = "http://localhost:5246/api/items";

  useEffect(() => {
    loadTasks();
  }, []);

  async function loadTasks() {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(apiUrl, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTodos(res.data);
    } catch (err) {
      console.error("‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊ò◊¢◊ô◊†◊™ ◊î◊û◊©◊ô◊û◊ï◊™:", err);
    }
  }

  async function addTask(e) {
    e.preventDefault();
    if (!newTask.trim()) return;
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        apiUrl,
        { name: newTask, isComplete: false },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setNewTask("");
      loadTasks();
    } catch (err) {
      console.error("‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊î◊ï◊°◊§◊™ ◊û◊©◊ô◊û◊î:", err);
    }
  }

  async function toggleComplete(id, isComplete) {
    try {
      const token = localStorage.getItem("token");
      await axios.put(
        `${apiUrl}/${id}`,
        { isComplete: !isComplete },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      loadTasks();
    } catch (err) {
      console.error("‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊¢◊ì◊õ◊ï◊ü ◊û◊©◊ô◊û◊î:", err);
    }
  }

  async function deleteTask(id) {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`${apiUrl}/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      loadTasks();
    } catch (err) {
      console.error("‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊û◊ó◊ô◊ß◊™ ◊û◊©◊ô◊û◊î:", err);
    }
  }

  return (
    <div className="todo-container fade-in">
      <header className="todo-header">
        <h1>‚ú® Task Manager Cloud ‚ú®</h1>
        <div className="user-info">
          <span>◊©◊ú◊ï◊ù, {userName}</span>
          <button className="logout-btn" onClick={onLogout}>
            ◊î◊™◊†◊™◊ß üö™
          </button>
        </div>
      </header>

      <form className="task-form" onSubmit={addTask}>
        <input
          type="text"
          placeholder="◊î◊ï◊°◊£ ◊û◊©◊ô◊û◊î ◊ó◊ì◊©◊î..."
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <button type="submit" className="add-btn">
          ‚ûï ◊î◊ï◊°◊£
        </button>
      </form>

      <ul className="task-list">
        {todos.map((todo) => (
          <li
            key={todo.id}
            className={`task-item ${todo.isComplete ? "completed" : ""}`}
          >
            <span
              className="task-name"
              onClick={() => toggleComplete(todo.id, todo.isComplete)}
            >
              {todo.name}
            </span>
            <button
              className="delete-btn"
              onClick={() => deleteTask(todo.id)}
              title="◊û◊ó◊ß ◊û◊©◊ô◊û◊î"
            >
              ‚ùå
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
