import React, { useState, useEffect } from "react";
import service from "./service";
import "./App.css";

function App() {
  const [todos, setTodos] = useState([]);
  const [userName, setUserName] = useState("");
  const [search, setSearch] = useState("");
  const [showAdd, setShowAdd] = useState(false);
  const [newTask, setNewTask] = useState("");
  const [editing, setEditing] = useState(null);
  const [editName, setEditName] = useState("");
  const [editComplete, setEditComplete] = useState(false);

  // ◊ò◊¢◊ô◊†◊™ ◊†◊™◊ï◊†◊ô ◊û◊©◊™◊û◊©
  useEffect(() => {
    const user = service.getCurrentUser();
    if (user) setUserName(user.userName);
    loadTodos();
  }, []);

  // ◊ò◊¢◊ô◊†◊™ ◊û◊©◊ô◊û◊ï◊™
  async function loadTodos() {
    try {
      const data = await service.getTasks();
      setTodos(data);
    } catch {
      console.error("Error loading tasks");
    }
  }

  // ◊î◊ï◊°◊§◊™ ◊û◊©◊ô◊û◊î
  async function addTodo(e) {
    e.preventDefault();
    if (!newTask.trim()) return;

    await service.addTask(newTask);
    setNewTask("");
    setShowAdd(false);
    loadTodos();
  }

  // ◊û◊ó◊ô◊ß◊î
  async function deleteTodo(id) {
    await service.deleteTask(id);
    loadTodos();
  }

  // ◊§◊™◊ô◊ó◊™ ◊¢◊®◊ô◊õ◊î
  function openEdit(todo) {
    setEditing(todo.id);
    setEditName(todo.name);
    setEditComplete(todo.isComplete);
  }

  // ◊©◊û◊ô◊®◊™ ◊¢◊®◊ô◊õ◊î
  async function saveEdit() {
    await service.updateTaskFull(editing, editName, editComplete);
    setEditing(null);
    loadTodos();
  }

  const filtered = todos.filter((t) =>
    t.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="todo-card fade-in">
      <h1 className="title">◊©◊ú◊ï◊ù, {userName} üëã</h1>

      {/* ◊ó◊ô◊§◊ï◊© */}
      <input
        className="search-box"
        type="text"
        placeholder="üîç ◊ó◊ô◊§◊ï◊© ◊û◊©◊ô◊û◊î..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      {/* ◊õ◊§◊™◊ï◊® ◊î◊ï◊°◊§◊î */}
      <button className="add-toggle" onClick={() => setShowAdd(!showAdd)}>
        {showAdd ? "◊°◊í◊ô◊®◊î ‚úñ" : "‚ûï ◊î◊ï◊°◊£ ◊û◊©◊ô◊û◊î"}
      </button>

      {/* ◊ò◊ï◊§◊° ◊î◊ï◊°◊§◊™ ◊û◊©◊ô◊û◊î */}
      {showAdd && (
        <form onSubmit={addTodo} className="add-form">
          <input
            type="text"
            placeholder="◊©◊ù ◊î◊û◊©◊ô◊û◊î..."
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
            required
          />
          <button type="submit" className="add-btn">◊î◊ï◊°◊£</button>
        </form>
      )}

      {/* ◊®◊©◊ô◊û◊™ ◊û◊©◊ô◊û◊ï◊™ */}
      <ul className="task-list">
        {filtered.map((todo) => (
          <li key={todo.id} className="task-row">

            {editing === todo.id ? (
              <div className="edit-box">
                <input
                  value={editName}
                  onChange={(e) => setEditName(e.target.value)}
                />

                <label className="check-label">
                  <input
                    type="checkbox"
                    checked={editComplete}
                    onChange={(e) => setEditComplete(e.target.checked)}
                  />
                  ◊û◊©◊ô◊û◊î ◊î◊ï◊©◊ú◊û◊î
                </label>

                <button className="save-btn" onClick={saveEdit}>üíæ ◊©◊û◊ô◊®◊î</button>
              </div>
            ) : (
              <>
                <span
                  className={`task-name ${todo.isComplete ? "done" : ""}`}
                >
                  {todo.name}
                </span>

                <div className="task-actions">
                  <button className="edit-btn" onClick={() => openEdit(todo)}>
                    ‚úèÔ∏è
                  </button>
                  <button className="delete-btn" onClick={() => deleteTodo(todo.id)}>
                    ‚ùå
                  </button>
                </div>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
