import axios from "axios";

const apiUrl = "http://localhost:5246/api";

// ✅ יצירת מופע axios עם הגדרות בסיסיות
const api = axios.create({
  baseURL: apiUrl,
  headers: {
    "Content-Type": "application/json",
  },
});

// ✅ הוספת interceptor — מוסיף את הטוקן לכל בקשה
api.interceptors.request.use((config) => {
  const token = localStorage.getItem("token");
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// ✅ טיפול אוטומטי בשגיאת 401 (כאשר הטוקן לא תקף)
api.interceptors.response.use(
  (res) => res,
  (err) => {
    if (err.response && err.response.status === 401) {
      localStorage.removeItem("token");
      window.location.href = "/login"; // מפנה לדף התחברות
    }
    return Promise.reject(err);
  }
);

export default {
  // ---------- Auth ----------
  register: async (username, password) => {
    const res = await api.post("/auth/register", {
      userName: username,
      passwordHash: password,
    });
    const { token, user } = res.data;
    localStorage.setItem("token", token);
    localStorage.setItem("user", JSON.stringify(user));
    return user;
  },

  login: async (username, password) => {
    const res = await api.post("/auth/login", {
      userName: username,
      passwordHash: password,
    });
    const { token, user } = res.data;
    localStorage.setItem("token", token);
    localStorage.setItem("user", JSON.stringify(user));
    return user;
  },

  logout: () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
  },

  getCurrentUser: () => {
    const user = localStorage.getItem("user");
    return user ? JSON.parse(user) : null;
  },

  // ---------- Tasks ----------
  getTasks: async () => {
    const res = await api.get("/items");
    return res.data;
  },

  getTaskById: async (id) => {
    const res = await api.get(`/items/${id}`);
    return res.data;
  },

  addTask: async (name) => {
    const res = await api.post("/items", { name, isComplete: false });
    return res.data;
  },

  updateTask: async (id, isComplete) => {
    await api.put(`/items/${id}`, {
      isComplete: isComplete
    });
  },

  updateTaskFull: async (id, name, isComplete) => {
  const response = await api.put(`/items/${id}`, {
    name: name,
    isComplete: isComplete
  });
  return response.data;
},


  deleteTask: async (id) => {
    await api.delete(`/items/${id}`);
  },
};
