import React, { useEffect, useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
  useNavigate,
} from "react-router-dom";
import axios from "axios";
import Login from "./Login";
import Register from "./Register";
import Welcome from "./welcome";
import "./App.css";

function App() {
  return (
    <Router>
      <MainRoutes />
    </Router>
  );
}

function MainRoutes() {
  const navigate = useNavigate();
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // ◊ú◊ë◊ì◊ï◊ß ◊ê◊ù ◊ô◊© ◊ò◊ï◊ß◊ü ◊õ◊©◊†◊ò◊¢◊ü
  useEffect(() => {
    const token = localStorage.getItem("token");
    setIsAuthenticated(!!token);
  }, []);

  // ◊î◊™◊ó◊ë◊®◊ï◊™ ◊û◊ï◊¶◊ú◊ó◊™ ‚Üí ◊ú◊¢◊û◊ï◊ì welcome
  const handleLoginSuccess = () => {
    setIsAuthenticated(true);
    navigate("/welcome");
  };

  // ◊î◊®◊©◊û◊î ◊û◊ï◊¶◊ú◊ó◊™ ‚Üí ◊ú◊¢◊û◊ï◊ì login
  const handleRegisterSuccess = () => {
    navigate("/login");
  };

  // ◊î◊™◊†◊™◊ß◊ï◊™ ‚Üí ◊†◊ô◊ß◊ï◊ô ◊ï◊©◊ô◊ú◊ï◊ó ◊ú-login
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userName");
    setIsAuthenticated(false);
    navigate("/login");
  };

  return (
    <Routes>
      <Route
        path="/"
        element={<Navigate to={isAuthenticated ? "/welcome" : "/login"} />}
      />

      <Route
        path="/login"
        element={
          isAuthenticated ? (
            <Navigate to="/welcome" />
          ) : (
            <Login onLoginSuccess={handleLoginSuccess} />
          )
        }
      />

      <Route
        path="/register"
        element={
          isAuthenticated ? (
            <Navigate to="/welcome" />
          ) : (
            <Register onRegisterSuccess={handleRegisterSuccess} />
          )
        }
      />

      <Route
        path="/welcome"
        element={
          isAuthenticated ? (
            <Welcome onLogout={handleLogout} />
          ) : (
            <Navigate to="/login" />
          )
        }
      />

      <Route
        path="/app"
        element={
          isAuthenticated ? (
            <TodoApp onLogout={handleLogout} />
          ) : (
            <Navigate to="/login" />
          )
        }
      />

      <Route
        path="*"
        element={
          <h2 style={{ textAlign: "center", marginTop: "40px" }}>
            404 - ◊î◊ì◊£ ◊ú◊ê ◊†◊û◊¶◊ê üòÖ
          </h2>
        }
      />
    </Routes>
  );
}

// =======================
// ◊ú◊ï◊ó ◊î◊û◊©◊ô◊û◊ï◊™ (TodoApp)
// =======================
function TodoApp({ onLogout }) {
  const [todos, setTodos] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [loading, setLoading] = useState(false);
  const userName = localStorage.getItem("userName") || "";
  const apiUrl = "http://localhost:5246/api/items";

  useEffect(() => {
    loadTasks();
  }, []);

  async function loadTasks() {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");
      const res = await axios.get(apiUrl, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTodos(res.data);
    } catch (err) {
      console.error("◊©◊í◊ô◊ê◊î ◊ë◊ò◊¢◊ô◊†◊™ ◊û◊©◊ô◊û◊ï◊™:", err);
    } finally {
      setLoading(false);
    }
  }

  async function addTask(e) {
    e.preventDefault();
    if (!newTask.trim()) return;
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        apiUrl,
        { name: newTask, isComplete: false },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setNewTask("");
      await loadTasks();
    } catch (err) {
      console.error("◊©◊í◊ô◊ê◊î ◊ë◊î◊ï◊°◊§◊™ ◊û◊©◊ô◊û◊î:", err);
    }
  }

  async function toggleComplete(todo) {
    try {
      const token = localStorage.getItem("token");
      await axios.put(
        `${apiUrl}/${todo.id}`,
        {
          id: todo.id,
          name: todo.name,
          isComplete: !todo.isComplete,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      await loadTasks();
    } catch (err) {
      console.error("◊©◊í◊ô◊ê◊î ◊ë◊¢◊ì◊õ◊ï◊ü ◊û◊©◊ô◊û◊î:", err);
    }
  }

  async function deleteTask(id) {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`${apiUrl}/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      await loadTasks();
    } catch (err) {
      console.error("◊©◊í◊ô◊ê◊î ◊ë◊û◊ó◊ô◊ß◊™ ◊û◊©◊ô◊û◊î:", err);
    }
  }

  return (
    <div className="todo-container fade-in">
      <header className="todo-header">
        <h1>üóÇÔ∏è ◊ú◊ï◊ó ◊î◊û◊©◊ô◊û◊ï◊™ ◊©◊ú◊ô</h1>
        <div className="user-info">
          <span>◊©◊ú◊ï◊ù, {userName}</span>
          <button className="logout-btn" onClick={onLogout}>
            ◊î◊™◊†◊™◊ß üö™
          </button>
        </div>
      </header>

      <form className="task-form" onSubmit={addTask}>
        <input
          type="text"
          placeholder="◊î◊ï◊°◊ô◊§◊ô ◊û◊©◊ô◊û◊î ◊ó◊ì◊©◊î..."
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <button type="submit" className="add-btn">
          ‚ûï
        </button>
      </form>

      {loading ? (
        <p style={{ textAlign: "center" }}>◊ò◊ï◊¢◊ü ◊û◊©◊ô◊û◊ï◊™...</p>
      ) : todos.length === 0 ? (
        <p style={{ textAlign: "center" }}>◊ê◊ô◊ü ◊û◊©◊ô◊û◊ï◊™ ◊¢◊ì◊ô◊ô◊ü ‚ú®</p>
      ) : (
        <ul className="task-list">
          {todos.map((todo) => (
            <li
              key={todo.id}
              className={`task-item ${todo.isComplete ? "completed" : ""}`}
            >
              <span
                className="task-name"
                onClick={() => toggleComplete(todo)}
                title="◊°◊ô◊û◊ï◊ü/◊ë◊ô◊ò◊ï◊ú ◊î◊ï◊©◊ú◊û◊î"
              >
                {todo.name}
              </span>
              <button
                className="delete-btn"
                onClick={() => deleteTask(todo.id)}
                title="◊û◊ó◊ß ◊û◊©◊ô◊û◊î"
              >
                ‚ùå
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
